mapper.insert:

    SELECT @@IDENTITY


insert into app (name, contacts_id, effective_date, expire_date, remark, unsign_file, sign_file, create_time, update_time, is_delete, cert_id, valid, is_unsign)
values (#{name,jdbcType=VARCHAR}, #{contactsId, jdbcType=BIGINT}, #{effectiveDate,jdbcType=DATE},
#{expireDate,jdbcType=DATE}, #{remark,jdbcType=VARCHAR},  #{unsignFile,jdbcType=VARCHAR}, #{signFile,jdbcType=VARCHAR},
#{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{isDelete,jdbcType=INTEGER}, #{certId,jdbcType=BIGINT}, #{valid,jdbcType=INTEGER}, #{isUnsign,jdbcType=INTEGER}
)

mapper.select:
  	select  from app a left join contacts c on a.contacts_id = c.id


			and a.name = #{name,jdbcType=VARCHAR} 


			and a.name like concat('%', #{qname,jdbcType=VARCHAR}, '%') 


	    	and a.effective_date >= #{startEffectiveDate, jdbcType=DATE}


	    	and a.effective_date <= #{endEffectiveDate, jdbcType=DATE}


	    	and a.expire_date >= #{startExpireDate, jdbcType=DATE}


	    	and a.expire_date <= #{endExpireDate, jdbcType=DATE}


	    	and a.create_time >= #{startCreateTime, jdbcType=TIMESTAMP}


	    	and a.create_time <= #{endCreateTime, jdbcType=TIMESTAMP}


	    	and a.update_time >= #{startUpdateTime, jdbcType=TIMESTAMP}


	    	and a.update_time <= #{endUpdateTime, jdbcType=TIMESTAMP}


			and a.is_delete = #{isDelete,jdbcType=INTEGER}


			and a.id <> #{id,jdbcType=BIGINT}


			and a.cert_id = #{certId,jdbcType=BIGINT}


			and c.wechat like concat('%', #{wechat,jdbcType=VARCHAR}, '%') 


			and a.create_time = a.update_time


			and a.create_time <> a.update_time


			and a.valid = #{valid,jdbcType=INTEGER}


			and a.is_unsign = #{isUnsign,jdbcType=INTEGER}





  			order by ${custom_order_sql}


  			order by a.id desc
